import React, { useState, useEffect } from "react";
import "./WeatherForecast.css";
import WeatherForecastDay from "./WeatherForecastDay";
import "./WeatherSearch.css";
import axios from "axios";

export default function WeatherForecast(event) {
  let forecast = event.data.allData.data.daily;
  const [loaded, setLoaded] = useState(false);
  const [city, setCity] = useState(event.data.allData.data.city);

  function handleResponse() {
    setLoaded(true);
  }

  if (loaded) {
    return (
      <div className="WeatherForecast">
        <div className="row WeatherForecast">
          <div className="col">
            <div></div>
            <WeatherForecastDay forecast={forecast[1]} />
          </div>
          <div className="col">
            <WeatherForecastDay forecast={forecast[2]} />
          </div>
          <div className="col">
            <WeatherForecastDay forecast={forecast[3]} />
          </div>
          <div className="col">
            <WeatherForecastDay forecast={forecast[4]} />
          </div>
          <div className="col">
            <WeatherForecastDay forecast={forecast[5]} />
          </div>
        </div>
      </div>
    );
  } else {
    let url = `https://api.shecodes.io/weather/v1/forecast?query=${city}
        &key=edf069311acf2bebo10f4bbbc53249t3&units=metric`;
    axios.get(url, { signal: AbortSignal.timeout(1000) }).then(handleResponse);
    return null;
  }
}
