import React, { useState } from "react";
import WeatherIcon from "./WeatherIcon";
import axios from "axios";
import "./WeatherForecast.css";
import FormattedDate from "./FormattedDate";
import WeatherForecastDay from "./weatherForecastDay";
s
export default function WeatherForecast(event) {
  let url = `https://api.shecodes.io/weather/v1/forecast?query=${event.data.data.cities}&key=edf069311acf2bebo10f4bbbc53249t3&units=metric`;
  axios.get(url).then(handleResponse);

  const [forecast, setForecast] = useState({});
  const [loaded, setLoaded] = useState(false);

  function handleResponse(response) {
    setForecast(response.data.daily);
    setLoaded(true);
    console.log(response);
  }

  return (
    <div className="WeatherForecast">
      <div className="row">
        <div className="col">
          <WeatherForecastDay data={forecast[0]} />
          <div className="forecastDay">
            <FormattedDate date={weather.date} />
          </div>
          <WeatherIcon code={weather.icon} size={52} />
          <div className="forecastTemperatures">
            <span className="forecastTemperatureMax">
              {weather.temperatureMaximum}°
            </span>
            <span className="forecastTemperatureMin">
              {" "}
              {weather.temperatureMinimum}°
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}
