import React, { useState } from "react";
import axios from "axios";
import "./App.css";

export default function WeatherSearch() {
  let [city, setCity] = useState(" ");
  const [weather, setWeather] = useState({});
  const [loaded, setLoaded] = useState(false);
  function changeCity(event) {
    event.preventDefault();
    setCity(event.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    let url = `https://api.shecodes.io/weather/v1/forecast?query=${city}&key=edf069311acf2bebo10f4bbbc53249t3&units=metric`;
    axios.get(url).then(displayWeather);
  }

  function displayWeather(response) {
    console.log(response);
    setLoaded(true);
    setWeather({
      temperature: response.data.daily[0].temperature.day,
      wind: response.data.daily[0].wind.speed,
      humidity: response.data.daily[0].temperature.humidity,
      description: response.data.daily[0].condition.description,
      icon: response.data.daily[0].condition.icon_url,
      country: response.data.country,
    });
  }

  let form = (
    <form onSubmit={handleSubmit}>
      <input type="search" placeholder="Enter a city" onChange={changeCity} />
      <input type="submit" value="Search" />
    </form>
  );

  if (loaded) {
    return (
      <div>
        {form}
        <h1>
          {city}, {weather.country}
        </h1>
        <h2>Temperature: {weather.temperature}Â°C</h2>
        <h2>Humidity: {weather.humidity}%</h2>
        <h2>Wind: {weather.wind}km/h</h2>
        <h2>Description: {weather.description}</h2>
        <img src={weather.icon} alt="weather icon" />
      </div>
    );
  } else {
    return form;
  }
}
