import React, { useState } from "react";
import WeatherIcon from "./WeatherIcon";
import axios from "axios";
import "./WeatherForecast.css";
import FormattedDate from "./FormattedDate";

export default function WeatherForecast(event) {
  let url = `https://api.shecodes.io/weather/v1/forecast?query=${event.data.data.cities}&key=edf069311acf2bebo10f4bbbc53249t3&units=metric`;
  axios.get(url).then(handleResponse);

  const [weather, setWeather] = useState({});

  function handleResponse(response) {
    setWeather({
      temperature: Math.round(response.data.daily[1].temperature),
      wind: Math.round(response.data.daily[1].wind.speed),
      humidity: response.data.daily[1].temperature.humidity,
      description: response.data.daily[1].condition.description,
      icon: response.data.daily[1].condition.icon,
      country: response.data.country,
      cities: response.data.city,
      date: new Date(response.data.daily[1].time * 1000),
    });
    console.log(response);
  }

  return (
    <div className="WeatherForecast">
      <div className="row">
        <div className="col-2">
          <div className="forecastDay"></div>
          <WeatherIcon code="snow-night" size={52} />
          <div className="forecastTemperatures">
            <span className="forecastTemperatureMin">
              {weather.temperature}
            </span>
            <span className="forecastTemperatureMax">19Â°</span>
          </div>
        </div>
      </div>
    </div>
  );
}
